import React from 'react'

const reducer = (state, action) => {
    switch (action.type) {
        case "GET_LOADING":
            return{
                ...state,
                isLoading: true,
            };

        case "GET_STORIES":
            return {
                ...state,
                isLoading: false,
                hits: action.payLoad.hits,
                nbPages: action.payLoad.nbPages,
            };
        case "REMOVE_POST":
            return{
                ...state,
                hits: state.hits.filter((curElem) => curElem.objectID != action.payLoad),
            };
        case "SEARCH_QUERY":
            return{
                ...state,
                query: action.payLoad,
            };
        
        case "NEXT_PAGE":
            return{
                ...state,
                page: state.page + 1,
            };

        case "PREV_PAGE":
            return{
                ...state,
                page: state.page -1,
            }

    }
  return state;
}

export default reducer
