import React from 'react'

const reducer = (state, action) => {
    switch (action.type) {
        case "GET_LOADING":
            return{
                ...state,
                isLoading: true,
            };

        case "GET_STORIES":
            return {
                ...state,
                isLoading: false,
                hits: action.payLoad.hits,
                nbPages: action.payLoad.nbPages,
            };
            case "REMOVE_POST":
                return{
                    ...state,
                    hits: state.hits.filter((curElem) => curElem.objectID != action.payLoad),
                };
    }
  return state;
}

export default reducer
