import React from 'react';
import { useGlobalHook } from './Context';

const Stories = () => {

  const { hits, nbPages } = useGlobalHook();


if (isLoading) {
  return(
    <>
      <h1>Loading.....</h1>
    </>
  );
}

  return (
    <>
      <h2>My Tech News Website</h2>
      {hits.map((curPost) => {
        return <h2>{curPost.title}</h2>;
      })}
    </>
  );
};

export default Stories
