import React from 'react'

const reducer = (state, action) => {
    switch (action.type) {
        case "GET_LOADING":
            return{
                ...state,
                isLoading:true,
            };

        case "GET_STORIES":
            return {
                ...state,
                hits: action.payLoad.hits,
                nbPages: action.payLoad.nbPages,
            };
    }
  return state;
}

export default reducer
